import"./modulepreload-polyfill-3cfb730f.js";const p=document.getElementById("target-date"),v=document.getElementById("confirm-button"),n=document.querySelector('[data-value="days"]'),o=document.querySelector('[data-value="hours"]'),a=document.querySelector('[data-value="mins"]'),r=document.querySelector('[data-value="secs"]');let s;v.addEventListener("click",c=>{c.preventDefault();const l=p.value,e=new Date(l);if(e<=new Date){alert("Будь ласка, вибери дату в майбутньому!");return}s=setInterval(()=>{const u=new Date,t=e.getTime()-u.getTime();if(t<=0){clearInterval(s),n.textContent=o.textContent=a.textContent=r.textContent="00";return}const d=Math.floor(t/(1e3*60*60*24)),i=Math.floor(t%(1e3*60*60*24)/(1e3*60*60)),m=Math.floor(t%(1e3*60*60)/(1e3*60)),S=Math.floor(t%(1e3*60)/1e3);n.textContent=String(d).padStart(2,"0"),o.textContent=String(i).padStart(2,"0"),a.textContent=String(m).padStart(2,"0"),r.textContent=String(S).padStart(2,"0")},1e3)});
