import"./modulepreload-polyfill-3cfb730f.js";const v=document.getElementById("target-date"),y=document.getElementById("confirm-button"),n=document.querySelector('[data-value="days"]'),o=document.querySelector('[data-value="hours"]'),a=document.querySelector('[data-value="mins"]'),r=document.querySelector('[data-value="secs"]');let s;y.addEventListener("click",c=>{c.preventDefault();const l=v.value,e=new Date(l),u=new Date,d=24*60*60*1e3;if(e.getTime()-u.getTime()<d){alert("Дата не повина бути менше 1 дня!");return}s=setInterval(()=>{const i=new Date,t=e.getTime()-i.getTime();if(t<=0){clearInterval(s),n.textContent=o.textContent=a.textContent=r.textContent="00";return}const m=Math.floor(t/(1e3*60*60*24)),S=Math.floor(t%(1e3*60*60*24)/(1e3*60*60)),g=Math.floor(t%(1e3*60*60)/(1e3*60)),p=Math.floor(t%(1e3*60)/1e3);n.textContent=String(m).padStart(2,"0"),o.textContent=String(S).padStart(2,"0"),a.textContent=String(g).padStart(2,"0"),r.textContent=String(p).padStart(2,"0")},1e3)});
